import { pickNonNull } from '../../pick-non-null.js';

/**
 * GET /products/zones
 * @param {Object} params
 */
export function getAllZones() {
  // no multipath for /self or /organisations/{id}
  return Promise.resolve({
    method: 'get',
    url: `/v4/products/zones`,
    headers: { Accept: 'application/json' },
    // no query params
    // no body
  });
}
